)

func transcribe_mic(microphone *Microphone, whispercpp *Whispercpp, user_msg *Editbox) error {
	if !microphone.Enable || !microphone.Triggered {
		return nil // Do nothing if microphone is not enabled and triggered
	}
	audioBytes, err := ioutil.ReadFile(microphone.Path)
	if err != nil {
		return err // Return the error if reading file fails
	}
	transcribedText, err := whispercpp.TranscribeBlob(audioBytes)
	if err != nil {
		return err // Return the error if transcription fails
	}
	user_msg.Value = transcribedText // Update user_msg with the transcribed text
	return nil
}
