import (
	"database/sql"
	"fmt"
	_ "github.com/mattn/go-sqlite3"
)

func Chat(button *Button, db *Tables, user_msg *Editbox, llamacpp *Llamacpp) error {
	if button.Triggered {
		database, err := sql.Open("sqlite3", db.Path)
		if err != nil {
			return err
		}
		defer database.Close()

		rows, err := database.Query("SELECT role, content FROM chat")
		if err != nil {
			return err
		}
		defer rows.Close()

		var messages []LlamaMessage
		for rows.Next() {
			var role, content string
			if err := rows.Scan(&role, &content); err != nil {
				return err
			}
			messages = append(messages, LlamaMessage{Role: role, Content: content})
		}
		if err := rows.Err(); err != nil {
			return err
		}

		userMessage := LlamaMessage{Role: "user", Content: user_msg.Value}
		messages = append(messages, userMessage)

		answer, err := llamacpp.GetAnswer(messages)
		if err != nil {
			return err
		}

		tx, err := database.Begin()
		if err != nil {
			return err
		}
		stmt, err := tx.Prepare("INSERT INTO chat(role, content) VALUES(?, ?)")
		if err != nil {
			return err
		}
		defer stmt.Close()

		_, err = stmt.Exec("user", user_msg.Value)
		if err != nil {
			tx.Rollback()
			return err
		}

		_, err = stmt.Exec("assistant", answer)
		if err != nil {
			tx.Rollback()
			return err
		}

		err = tx.Commit()
		if err != nil {
			return err
		}

		user_msg.Value = "" // Reset user message editbox after sending

		fmt.Printf("The assistant's answer is: %s\n", answer)
	}
	return nil
}
