import (
	"strconv"
)

func func_go(add_one *Button, counter *Text, sub_one *Button) error {
	if add_one.Clicked {
		c, _ := strconv.Atoi(counter.Label)
		c++
		counter.Label = strconv.Itoa(c)
		add_one.Clicked = false // Reset the button clicked state
	}
	if sub_one.Clicked {
		c, _ := strconv.Atoi(counter.Label)
		c--
		counter.Label = strconv.Itoa(c)
		sub_one.Clicked = false // Reset the button clicked state
	}
	return nil
}
