	if err != nil {
		return err
	}
	for _, file := range files {
		if filepath.Ext(file.Name()) == ".wav" {
			filePath := filepath.Join(disk_dir.Path, file.Name())
			transcription, err := whispercpp.TranscribeFile(filePath)
			if err != nil {
				return err
			}
			outputFilePath := strings.TrimSuffix(filePath, filepath.Ext(filePath)) + ".srt"
			err = ioutil.WriteFile(outputFilePath, []byte(transcription), 0644)
			if err != nil {
				return err
			}
		}
	}
	return nil
}
