import (
	"fmt"
)

func Summary(openai *Openai, editbox *Editbox, text *Text) error {
	message := []OpenaiMessage{
		{
			Role:    "system",
			Content: "Please summarize this article into a few bullet points:",
		},
		{
			Role:    "user",
			Content: editbox.Value,
		},
	}

	answer, err := openai.GetAnswer(message)
	if err != nil {
		return fmt.Errorf("failed to get summary: %w", err)
	}

	text.Label = answer
	return nil
}
